import { useState } from "react";
import React from 'react';
import allNote from '../notes';

const [tarea , setTarea] = useState({
    title: "",
    content: ""
});

function trackTarea(event){
    const {name , value} = event.target;

    setTarea(prevValue => {
        return{
            ...prevValue,
            [name]: value
        };
        
    });

};

function handleAdd(){
    allNote.push(tarea);
}

function CreateArea(){
    return <div>
        <form onChange = {trackTarea}>
            <input type = "text" placeholder= "Title" name= "title"></input><br></br>
            <textarea name="content" placeholder="Take a note..." rows="3" />
            <button onClick= {handleAdd} type= "submit">Add</button>
        </form>
    </div>
};

export default CreateArea;
